package week1.day1;

public class PrintPrimitiveDataTypes {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
 int myAge = 29;
 float myWeight = 66.555f;
 double myHeight = 5.1111111111d;
 long myPhoneNumber = 1234567891;
 char myInitial = 'P';
 boolean isMarried = true;
 short myRollNumber = (short) 32767;
 byte myPercentageOfMarks = 82;
 
 System.out.println("My Age is\n" + myAge);
 System.out.println("My Weight is\n" + myWeight);
 System.out.println("My Height is\n" + myHeight);
 System.out.println("My Phonenumber is\n" + myPhoneNumber);
 System.out.println("Am I Marries\n" + isMarried);
 System.out.println("My Initial is\n" + myInitial);
 System.out.println("My rollnumber is\n" + myRollNumber);
 System.out.println("My Percentage of mark is\n" + myPercentageOfMarks );
	}

}
