package week1.day2;

public class LearningIncrementDecrement {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int i;
		int j;
		int k;
		int l;

		for(i=0;i<5;i++)
		{
			System.out.println("The Incremental functions is = " + i);
			
		}
		
		System.out.println();
		for(j=4;j>=0;j--)
		{
			System.out.println("The Incremental functions is = " + j);
		}
		System.out.println();
		for(k=0;k<5;++k)
		{
			System.out.println("The Incremental functions is = " + k);
			
		}
		System.out.println();
		for(l=4;l>=0;--l)
		{
			System.out.println("The Incremental functions is = " + l);
			
		}
	}
}

